// src/components/OrderList.tsx
import React, { useState, useEffect } from "react";
import { Order } from "../types/order";
import { Button, Checkbox, IconButton, TextField } from "@material-ui/core";
import { deleteOrders, updateOrder } from "../services/api";
import EditIcon from "@material-ui/icons/Edit";
import "./OrderList.css";

const OrderList: React.FC<{
  orders: Order[];
  onDelete: () => void;
  onEdit: () => void;
}> = ({ orders, onDelete, onEdit }) => {
  const [selectedOrders, setSelectedOrders] = useState<Set<string>>(new Set());
  const [selectAll, setSelectAll] = useState<boolean>(false);
  const [editOrderId, setEditOrderId] = useState<string | null>(null);
  const [editOrder, setEditOrder] = useState<Order | null>(null);

  useEffect(() => {
    console.log("Orders updated: ", orders);
  }, [orders]);

  const handleDelete = async () => {
    console.log("Selected Orders for deletion: ", Array.from(selectedOrders));
    try {
      await deleteOrders(Array.from(selectedOrders));
      setSelectedOrders(new Set());
      onDelete();
    } catch (error) {
      console.error("Error deleting orders:", error);
    }
  };

  const handleSelect = (orderId: string) => {
    console.log(`Toggling selection for order with orderId: ${orderId}`);
    setSelectedOrders((prevSelectedOrders) => {
      const newSelectedOrders = new Set(prevSelectedOrders);
      if (newSelectedOrders.has(orderId)) {
        newSelectedOrders.delete(orderId);
      } else {
        newSelectedOrders.add(orderId);
      }
      console.log(
        `Current selected orders: ${Array.from(newSelectedOrders).join(", ")}`
      );
      return newSelectedOrders;
    });
  };

  const handleSelectAll = () => {
    if (selectAll) {
      setSelectedOrders(new Set());
    } else {
      const allOrderIds = orders.map((order) => order.orderId);
      setSelectedOrders(new Set(allOrderIds));
    }
    setSelectAll(!selectAll);
  };

  const handleEdit = (order: Order) => {
    setEditOrderId(order.orderId);
    setEditOrder(order);
  };

  const handleEditChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (editOrder) {
      const { name, value } = e.target;
      setEditOrder({ ...editOrder, [name]: value });
    }
  };

  const handleEditSubmit = async () => {
    if (editOrder) {
      try {
        const payload = {
          orderId: editOrder.orderId,
          orderType: editOrder.orderType,
          customerName: editOrder.customerName,
          createdDate: editOrder.createdDate,
          createdByUsername: editOrder.createdByUsername, // Ensure it matches the Order type
        };
        console.log("Updating order with data:", payload);
        await updateOrder(payload);
        setEditOrderId(null);
        setEditOrder(null);
        onEdit();
      } catch (error) {
        console.error("Error updating order:", error);
      }
    }
  };

  const handleCancelEdit = () => {
    setEditOrderId(null);
    setEditOrder(null);
  };

  return (
    <div className="order-list">
      <div className="order-list-controls">
        <TextField
          label="Customer Search"
          variant="outlined"
          size="small"
          style={{ marginRight: "10px" }}
        />
        <Button
          variant="contained"
          color="primary"
          className="create-order-btn"
        >
          + Create Order
        </Button>
        <Button
          variant="contained"
          className="delete-selected-btn"
          onClick={handleDelete}
          disabled={selectedOrders.size === 0}
        >
          Delete Selected
        </Button>
        <TextField
          select
          label="Order Type"
          variant="outlined"
          size="small"
          style={{ marginLeft: "10px" }}
        >
          <option value="">All</option>
          <option value="Standard">Standard</option>
          <option value="SaleOrder">Sale Order</option>
          <option value="PurchaseOrder">Purchase Order</option>
          <option value="TransferOrder">Transfer Order</option>
          <option value="ReturnOrder">Return Order</option>
        </TextField>
      </div>
      <table>
        <thead>
          <tr>
            <th>
              <Checkbox checked={selectAll} onChange={handleSelectAll} />
            </th>
            <th>Order ID</th>
            <th>Creation Date</th>
            <th>Created By</th>
            <th>Order Type</th>
            <th>Customer</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {orders.map((order) => (
            <tr key={order.orderId}>
              <td>
                <Checkbox
                  checked={selectedOrders.has(order.orderId)}
                  onChange={() => handleSelect(order.orderId)}
                />
              </td>
              {editOrderId === order.orderId ? (
                <>
                  <td>{order.orderId}</td>
                  <td>
                    <TextField
                      name="createdDate"
                      value={editOrder?.createdDate || ""}
                      onChange={handleEditChange}
                      fullWidth
                    />
                  </td>
                  <td>
                    <TextField
                      name="createdByUsername"
                      value={editOrder?.createdByUsername || ""}
                      onChange={handleEditChange}
                      fullWidth
                    />
                  </td>
                  <td>
                    <TextField
                      name="orderType"
                      value={editOrder?.orderType || ""}
                      onChange={handleEditChange}
                      fullWidth
                    />
                  </td>
                  <td>
                    <TextField
                      name="customerName"
                      value={editOrder?.customerName || ""}
                      onChange={handleEditChange}
                      fullWidth
                    />
                  </td>
                  <td>
                    <Button onClick={handleEditSubmit}>Save</Button>
                    <Button onClick={handleCancelEdit}>Cancel</Button>
                  </td>
                </>
              ) : (
                <>
                  <td>{order.orderId}</td>
                  <td>{order.createdDate}</td>
                  <td>{order.createdByUsername}</td>
                  <td>{order.orderType}</td>
                  <td>{order.customerName}</td>
                  <td className="actions">
                    <IconButton onClick={() => handleEdit(order)}>
                      <EditIcon />
                    </IconButton>
                  </td>
                </>
              )}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default OrderList;
