import React, { useEffect, useState } from "react";
import {
  Container,
  Typography,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Button,
  TextField,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
} from "@material-ui/core";
import OrderForm from "./components/OrderForm";
import OrderList from "./components/OrderList";
import { fetchOrders } from "./services/api";
import { Order } from "./types/order";
import "./App.css";

const App: React.FC = () => {
  const [orders, setOrders] = useState<Order[]>([]);
  const [filter, setFilter] = useState<string>("");
  const [search, setSearch] = useState<string>("");
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);
  const [open, setOpen] = useState(false);

  const loadOrders = async () => {
    setLoading(true);
    setError(null);
    try {
      const data = await fetchOrders();
      setOrders(data);
    } catch (error) {
      console.error("Error loading orders:", error);
      setError("Failed to load orders. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadOrders();
  }, []);

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const handleDeleteSelected = async () => {
    // Implement the delete logic
  };

  const filteredOrders = orders.filter(
    (order) =>
      (filter === "" || order.orderType === filter) &&
      (search === "" || order.orderId.includes(search))
  );

  return (
    <div className="container">
      <header>
        <Typography variant="h4" gutterBottom>
          Order Management
        </Typography>
      </header>
      <div className="order-list-controls">
        <TextField
          label="Customer Search"
          variant="outlined"
          size="small"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />
        <Button variant="contained" color="primary" onClick={handleClickOpen}>
          + Create Order
        </Button>
        <Button
          variant="contained"
          color="secondary"
          onClick={handleDeleteSelected}
        >
          Delete Selected
        </Button>
        <FormControl variant="outlined" style={{ minWidth: "150px" }}>
          <InputLabel>Order Type</InputLabel>
          <Select
            label="Order Type"
            displayEmpty
            value={filter}
            onChange={(e) => setFilter(e.target.value as string)}
          >
            <MenuItem value="">
              <em>Order Type</em>
            </MenuItem>
            <MenuItem value="Standard">Standard</MenuItem>
            <MenuItem value="SaleOrder">Sale Order</MenuItem>
            <MenuItem value="PurchaseOrder">Purchase Order</MenuItem>
            <MenuItem value="TransferOrder">Transfer Order</MenuItem>
            <MenuItem value="ReturnOrder">Return Order</MenuItem>
          </Select>
        </FormControl>
      </div>
      <Dialog open={open} onClose={handleClose}>
        <OrderForm onCreate={loadOrders} onClose={handleClose} />
      </Dialog>
      {loading && <Typography variant="h6">Loading...</Typography>}
      {error && (
        <Typography variant="h6" color="error">
          {error}
        </Typography>
      )}
      {!loading && !error && (
        <OrderList
          orders={filteredOrders}
          onDelete={loadOrders}
          onEdit={loadOrders}
        />
      )}
    </div>
  );
};

export default App;
