// src/components/OrderList.tsx
import React, { useState } from "react";
import { Order } from "../types/order";
import { Button, Checkbox } from "@material-ui/core";
import { deleteOrder } from "../services/api";
import "./OrderList.css";

const OrderList: React.FC<{ orders: Order[]; onDelete: () => void }> = ({
  orders,
  onDelete,
}) => {
  const [selectedOrders, setSelectedOrders] = useState<Set<string>>(new Set());

  const handleDelete = async () => {
    const deletePromises = Array.from(selectedOrders).map((id) =>
      deleteOrder(id)
    );
    try {
      await Promise.all(deletePromises);
      setSelectedOrders(new Set());
      onDelete();
    } catch (error) {
      console.error("Error deleting orders:", error);
    }
  };

  const handleSelect = (id: string) => {
    setSelectedOrders((prevSelectedOrders) => {
      const newSelectedOrders = new Set(prevSelectedOrders);
      if (newSelectedOrders.has(id)) {
        newSelectedOrders.delete(id);
      } else {
        newSelectedOrders.add(id);
      }
      return newSelectedOrders;
    });
  };

  return (
    <div className="order-list">
      <table>
        <thead>
          <tr>
            <th>Select</th>
            <th>Order Type</th>
            <th>Customer Name</th>
            <th>Created By</th>
            <th>Created Date</th>
          </tr>
        </thead>
        <tbody>
          {orders.map((order) => (
            <tr key={order.id}>
              <td>
                <Checkbox
                  checked={selectedOrders.has(order.id)}
                  onChange={() => handleSelect(order.id)}
                />
              </td>
              <td>{order.orderType}</td>
              <td>{order.customerName}</td>
              <td>{order.createdByUsername}</td>
              <td>{order.createdDate}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <Button
        variant="contained"
        color="secondary"
        onClick={handleDelete}
        disabled={selectedOrders.size === 0}
        style={{ marginTop: "20px" }}
      >
        Delete Selected
      </Button>
    </div>
  );
};

export default OrderList;
