// src/components/OrderFilter.tsx
import React from "react";
import {
  TextField,
  MenuItem,
  Select,
  InputLabel,
  FormControl,
} from "@material-ui/core";
import "./OrderFilter.css";

const OrderFilter: React.FC<{
  filter: string;
  setFilter: (filter: string) => void;
  search: string;
  setSearch: (search: string) => void;
}> = ({ filter, setFilter, search, setSearch }) => {
  return (
    <div className="order-filter">
      <FormControl fullWidth>
        <InputLabel id="order-filter-label">Filter by Type</InputLabel>
        <Select
          labelId="order-filter-label"
          value={filter}
          onChange={(e) => setFilter(e.target.value as string)}
        >
          <MenuItem value="">All</MenuItem>
          <MenuItem value="Standard">Standard</MenuItem>
          <MenuItem value="SaleOrder">Sale Order</MenuItem>
          <MenuItem value="PurchaseOrder">Purchase Order</MenuItem>
          <MenuItem value="TransferOrder">Transfer Order</MenuItem>
          <MenuItem value="ReturnOrder">Return Order</MenuItem>
        </Select>
      </FormControl>
      <TextField
        label="Search by ID"
        value={search}
        onChange={(e) => setSearch(e.target.value)}
        fullWidth
        margin="normal"
      />
    </div>
  );
};

export default OrderFilter;
