import React, { useEffect, useState } from "react";
import { Container, Typography } from "@material-ui/core";
import OrderForm from "./components/OrderForm";
import OrderList from "./components/OrderList";
import OrderFilter from "./components/OrderFilter";
import { fetchOrders } from "./services/api";
import { Order } from "./types/order";
import "./App.css";

const App: React.FC = () => {
  const [orders, setOrders] = useState<Order[]>([]);
  const [filter, setFilter] = useState<string>("");
  const [search, setSearch] = useState<string>("");
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);

  const loadOrders = async () => {
    setLoading(true);
    setError(null);
    try {
      const data = await fetchOrders();
      setOrders(data);
    } catch (error) {
      console.error("Error loading orders:", error);
      setError("Failed to load orders. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadOrders();
  }, []);

  const filteredOrders = orders.filter(
    (order) =>
      (filter === "" || order.orderType === filter) &&
      (search === "" || order.orderId.includes(search))
  );

  return (
    <div className="container">
      <header>
        <Typography variant="h4" gutterBottom>
          Order Management
        </Typography>
        <div>
          <button className="MuiButton-root MuiButton-contained MuiButton-containedPrimary">
            + CREATE ORDER
          </button>
          <button className="MuiButton-root MuiButton-contained MuiButton-containedSecondary">
            DELETE SELECTED
          </button>
        </div>
      </header>
      <OrderForm onCreate={loadOrders} />
      <OrderFilter
        filter={filter}
        setFilter={setFilter}
        search={search}
        setSearch={setSearch}
      />
      {loading && <Typography variant="h6">Loading...</Typography>}
      {error && (
        <Typography variant="h6" color="error">
          {error}
        </Typography>
      )}
      {!loading && !error && (
        <OrderList
          orders={filteredOrders}
          onDelete={loadOrders}
          onEdit={loadOrders}
        />
      )}
    </div>
  );
};

export default App;
