// src/components/OrderList.tsx
import React, { useState } from "react";
import { Order } from "../types/order";
import {
  Button,
  Checkbox,
  List,
  ListItem,
  ListItemText,
} from "@material-ui/core";
import { deleteOrder } from "../services/api";
import "./OrderList.css";

const OrderList: React.FC<{ orders: Order[]; onDelete: () => void }> = ({
  orders,
  onDelete,
}) => {
  const [selectedOrders, setSelectedOrders] = useState<string[]>([]);

  const handleDelete = async () => {
    const deletePromises = selectedOrders.map((id) => deleteOrder(id));
    await Promise.all(deletePromises);
    setSelectedOrders([]);
    onDelete();
  };

  const handleSelect = (id: string) => {
    setSelectedOrders((prevSelectedOrders) =>
      prevSelectedOrders.includes(id)
        ? prevSelectedOrders.filter((orderId) => orderId !== id)
        : [...prevSelectedOrders, id]
    );
  };

  const isSelected = (id: string) => selectedOrders.includes(id);

  return (
    <div className="order-list">
      <table>
        <thead>
          <tr>
            <th>Select</th>
            <th>Order Type</th>
            <th>Customer Name</th>
            <th>Created By</th>
            <th>Created Date</th>
          </tr>
        </thead>
        <tbody>
          {orders.map((order) => (
            <tr key={order.id}>
              <td>
                <Checkbox
                  checked={isSelected(order.id)}
                  onChange={() => handleSelect(order.id)}
                />
              </td>
              <td>{order.orderType}</td>
              <td>{order.customerName}</td>
              <td>{order.createdByUsername}</td>
              <td>{order.createdDate}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <Button
        variant="contained"
        color="secondary"
        onClick={handleDelete}
        disabled={selectedOrders.length === 0}
        style={{ marginTop: "20px" }}
      >
        Delete Selected
      </Button>
    </div>
  );
};

export default OrderList;
