// src/components/OrderForm.tsx
import React, { useState } from "react";
import {
  Button,
  TextField,
  MenuItem,
  Select,
  InputLabel,
  FormControl,
} from "@material-ui/core";
import { createOrder } from "../services/api";

const OrderForm: React.FC<{ onCreate: () => void }> = ({ onCreate }) => {
  const [type, setType] = useState<string>("");
  const [customerName, setCustomerName] = useState<string>("");
  const [createdByUsername, setCreatedByUsername] = useState<string>("");
  const [error, setError] = useState<string | null>(null);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError(null);
    if (!type || !customerName || !createdByUsername) {
      setError("All fields are required");
      return;
    }
    try {
      await createOrder({ type, customerName, createdByUsername });
      onCreate();
    } catch (error) {
      console.error("Error creating order:", error);
      setError("Failed to create order. Please check the data and try again.");
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <FormControl fullWidth>
        <InputLabel id="order-type-label">Order Type</InputLabel>
        <Select
          labelId="order-type-label"
          value={type}
          onChange={(e) => setType(e.target.value as string)}
        >
          <MenuItem value="Standard">Standard</MenuItem>
          <MenuItem value="SaleOrder">Sale Order</MenuItem>
          <MenuItem value="PurchaseOrder">Purchase Order</MenuItem>
          <MenuItem value="TransferOrder">Transfer Order</MenuItem>
          <MenuItem value="ReturnOrder">Return Order</MenuItem>
        </Select>
      </FormControl>
      <TextField
        label="Customer Name"
        value={customerName}
        onChange={(e) => setCustomerName(e.target.value)}
        fullWidth
        margin="normal"
      />
      <TextField
        label="Created By Username"
        value={createdByUsername}
        onChange={(e) => setCreatedByUsername(e.target.value)}
        fullWidth
        margin="normal"
      />
      {error && <p style={{ color: "red" }}>{error}</p>}
      <Button type="submit" variant="contained" color="primary">
        Create Order
      </Button>
    </form>
  );
};

export default OrderForm;
