// src/services/api.ts
import axios from "axios";
import { Order } from "../types/order"; // Import the Order type

const API_KEY = "b7b77702-b4ec-4960-b3f7-7d40e44cf5f4";
const API_URL = "https://red-candidate-web.azurewebsites.net/api";

export const api = axios.create({
  baseURL: API_URL,
  headers: {
    ApiKey: API_KEY,
  },
});

export const fetchOrders = async () => {
  try {
    const response = await api.get("/orders");
    return response.data;
  } catch (error) {
    console.error("Error fetching orders:", error);
    throw error;
  }
};

export const createOrder = async (order: {
  orderType: string;
  customerName: string;
  createdByUsername: string;
}) => {
  try {
    const response = await api.post("/orders", order);
    return response.data;
  } catch (error) {
    console.error("Error creating order:", error);
    throw error;
  }
};

export const deleteOrder = async (id: string) => {
  try {
    const response = await api.delete(`/orders/${id}`);
    return response.data;
  } catch (error) {
    console.error("Error deleting order:", error);
    throw error;
  }
};

// New function to delete multiple orders
export const deleteOrders = async (ids: string[]) => {
  try {
    const response = await api.post("/orders/delete", ids); // Assuming the API endpoint for deleting multiple orders
    return response.data;
  } catch (error) {
    console.error("Error deleting orders:", error);
    throw error;
  }
};

// New function to update an order
export const updateOrder = async (order: Order) => {
  try {
    console.log(`API call to update order with ID: ${order.orderId}`);
    const response = await api.put(`/orders/${order.orderId}`, order);
    return response.data;
  } catch (error) {
    console.error("Error updating order:", error);
    throw error;
  }
};
